import { useState, useEffect, useRef } from 'react';
import { Handle, Position } from 'reactflow';

export const TextNode = ({ id, data }) => {
  const [textValue, setTextValue] = useState(data?.text || '{{input}}');
  const [variables, setVariables] = useState([]);
  const [size, setSize] = useState({ width: 200, height: 100 });
  const textRef = useRef(null);

  // --- Parse {{variable}} patterns ---
  useEffect(() => {
    const regex = /\{\{\s*([a-zA-Z_$][a-zA-Z0-9_$]*)\s*\}\}/g;
    const found = Array.from(textValue.matchAll(regex)).map((m) => m[1]);
    setVariables([...new Set(found)]);
  }, [textValue]);

  // --- Auto-resize node based on text content ---
  useEffect(() => {
    const el = textRef.current;
    if (el) {
      el.style.height = 'auto';
      el.style.width = 'auto';
      const newHeight = Math.min(el.scrollHeight + 20, 300);
      const newWidth = Math.min(Math.max(200, el.scrollWidth + 40), 400);
      setSize({ width: newWidth, height: newHeight });
    }
  }, [textValue]);

  return (
    <div
      className="vs-node"
      style={{
        width: size.width,
        height: size.height,
        position: 'relative',
        transition: 'width 0.15s ease, height 0.15s ease',
      }}
    >
      {/* Input handles for variables */}
      {variables.map((v, i) => (
        <Handle
          key={v}
          type="target"
          position={Position.Left}
          id={`${id}-${v}`}
          style={{
            top: `${(i + 1) * (100 / (variables.length + 1))}%`,
          }}
        />
      ))}

      {/* Output handle */}
      <Handle
        type="source"
        position={Position.Right}
        id={`${id}-output`}
        style={{ top: '50%' }}
      />

      <div className="vs-node__title">Text</div>

      <label className="vs-label" style={{ flex: 1 }}>
        Text:
        <textarea
          ref={textRef}
          className="vs-input"
          rows={3}
          value={textValue}
          onChange={(e) => setTextValue(e.target.value)}
          style={{
            resize: 'none',
            height: '100%',
            width: '100%',
            lineHeight: 1.4,
          }}
        />
      </label>
    </div>
  );
};
