import React from 'react';
import { Handle, Position } from 'reactflow';

/**
 * BaseNode - shared visual container for all nodes.
 * Automatically adjusts its height based on content.
 */
export const BaseNode = ({ id, title, inputs = [], outputs = [], content, width = 200 }) => {
  return (
    <div
      style={{
        width,
        border: '1px solid black',
        borderRadius: 8,
        backgroundColor: '#ce3434ff',
        padding: 8,
        fontSize: 12,
        display: 'flex',
        flexDirection: 'column',
        gap: 6,
        position: 'relative', // required for handles
      }}
    >
      {/* Handles: inputs on left, outputs on right */}
      {inputs.map((inp, i) => (
        <Handle
          key={inp.id}
          type="target"
          position={inp.position || Position.Left}
          id={`${id}-${inp.id}`}
          style={{
            top: inp.top || `${(i + 1) * (100 / (inputs.length + 1))}%`,
            background: '#111',
          }}
        />
      ))}

      {outputs.map((out, i) => (
        <Handle
          key={out.id}
          type="source"
          position={out.position || Position.Right}
          id={`${id}-${out.id}`}
          style={{
            top: out.top || `${(i + 1) * (100 / (outputs.length + 1))}%`,
            background: '#111',
          }}
        />
      ))}

      {/* Header */}
      <div style={{ fontWeight: 'bold', marginBottom: 4 }}>{title}</div>

      {/* Content grows freely â€” node height auto-adjusts */}
      <div
        style={{
          display: 'flex',
          flexDirection: 'column',
          gap: 6,
          alignItems: 'stretch',
        }}
      >
        {content}
      </div>
    </div>
  );
};
