import React from 'react';
import { Handle, Position } from 'reactflow';
import { useState } from 'react';


/**
 * Generic, reusable node wrapper.
 * Accepts:
 *  - title: string
 *  - inputs: [{ id, position, top }]
 *  - outputs: [{ id, position, top }]
 *  - content: JSX to render inside
 *  - width, height: numbers (optional)
 */
export const BaseNode = ({ id, title, inputs = [], outputs = [], content, width = 200,}) => {
  return (
    <div
      style={{
        width,
        border: '1px solid black',
        borderRadius: 6,
        background: '#fff',
        padding: 8,
        fontSize: 12,
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'space-between'
      }}
    >
      {/* Handles: inputs on left, outputs on right */}
      {inputs.map((inp, i) => (
        <Handle
          key={inp.id}
          type="target"
          position={inp.position || Position.Left}
          id={`${id}-${inp.id}`}
          style={{ top: inp.top || `${(i + 1) * (100 / (inputs.length + 1))}%` }}
        />
      ))}
      {outputs.map((out, i) => (
        <Handle
          key={out.id}
          type="source"
          position={out.position || Position.Right}
          id={`${id}-${out.id}`}
          style={{ top: out.top || `${(i + 1) * (100 / (outputs.length + 1))}%` }}
        />
      ))}

      {/* Header */}
      <div style={{ fontWeight: 'bold', marginBottom: 4 }}>{title}</div>

      <div
        style={{
          display: 'flex',
          flexDirection: 'column',
          gap: 6,
          alignItems: 'stretch',
        }}
      >
        {content}
      </div>
    </div>
  );
};
