import React from 'react';
import { BaseNode } from './baseNode';
import { useState } from 'react';


/**
 * Factory for creating nodes quickly.
 * Each node config defines:
 *  - title
 *  - inputs / outputs (array)
 *  - render: function({ id, data }) => JSX (the content area)
 */
export const createNode = (config) => {
  return function NodeComponent({ id, data }) {
    return (
      <BaseNode
        id={id}
        title={config.title}
        inputs={config.inputs}
        outputs={config.outputs}
        content={config.render({ id, data })}
      />
    );
  };
};
